// SPDX-License-Identifier: MIT
pragma solidity ^0.8.25;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title MockAavePool
 * @notice Mock contract simulating AAVE Pool's stake function for testing BridgeAndExecuteButton
 * @dev This contract accepts ERC20 tokens and tracks stakes per user
 */
contract MockAavePoolOptimism {
    using SafeERC20 for IERC20;

    // Events
    event Stake(
        address indexed asset,
        uint256 amount,
        address indexed onBehalfOf,
        uint16 indexed referralCode
    );

    event Withdraw(
        address indexed asset,
        uint256 amount,
        address indexed to
    );

    // Mapping: user => asset => staked amount
    mapping(address => mapping(address => uint256)) public stakes;

    // Total staked per asset
    mapping(address => uint256) public totalStaked;

    /**
     * @notice Stake assets into the pool
     * @param asset The address of the underlying asset to stake
     * @param amount The amount to be staked
     * @param onBehalfOf The address that will receive the stake credit
     * @param referralCode Code used to register the integrator originating the operation
     */
    function stake(
        address asset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external {
        require(asset != address(0), "Invalid asset address");
        require(amount > 0, "Amount must be greater than 0");
        require(onBehalfOf != address(0), "Invalid onBehalfOf address");

        // Transfer tokens from sender to this contract
        IERC20(asset).safeTransferFrom(msg.sender, address(this), amount);

        // Update stakes
        stakes[onBehalfOf][asset] += amount;
        totalStaked[asset] += amount;

        emit Stake(asset, amount, onBehalfOf, referralCode);
    }

    /**
     * @notice Withdraw staked assets from the pool
     * @param asset The address of the underlying asset to withdraw
     * @param amount The amount to be withdrawn
     * @param to The address that will receive the underlying
     * @return The final amount withdrawn
     */
    function withdraw(
        address asset,
        uint256 amount,
        address to
    ) external returns (uint256) {
        require(asset != address(0), "Invalid asset address");
        require(amount > 0, "Amount must be greater than 0");
        require(to != address(0), "Invalid to address");
        require(stakes[msg.sender][asset] >= amount, "Insufficient balance");

        // Update stakes
        stakes[msg.sender][asset] -= amount;
        totalStaked[asset] -= amount;

        // Transfer tokens to recipient
        IERC20(asset).safeTransfer(to, amount);

        emit Withdraw(asset, amount, to);

        return amount;
    }

    /**
     * @notice Get the staked balance for a user and asset
     * @param user The address of the user
     * @param asset The address of the asset
     * @return The staked balance
     */
    function getStakeBalance(address user, address asset) external view returns (uint256) {
        return stakes[user][asset];
    }

    /**
     * @notice Get the total staked amount for an asset
     * @param asset The address of the asset
     * @return The total staked amount
     * @dev This function is used to get the total staked amount for an asset
     */
    function getTotalStaked(address asset) external view returns (uint256) {
        return totalStaked[asset];
    }
}

